window.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".header__menu"),t=document.querySelectorAll(".header__menu-item"),a=document.querySelector(".hamburger");a.addEventListener("click",()=>{a.classList.toggle("hamburger__active"),e.classList.toggle("header__active")}),t.forEach(t=>{t.addEventListener("click",()=>{a.classList.toggle("hamburger__active"),e.classList.toggle("header__active")})})});